sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
	var workoutValues = g_form.getValue("workouts").split(',');

	var isLessThanFive = workoutValues.length < 5;
	var hasDuplicates = new Set(workoutValues).size !== workoutValues.length;

	if (isLessThanFive || hasDuplicates) {
		if (isLessThanFive) {
		g_form.addInfoMessage("Workout plans should contain a minimum of 5 workouts.");
		}

		if (hasDuplicates) {
		g_form.addInfoMessage("Workout plans should not contain duplicate workouts.");
		}

		return false;
	}

	return true;
}
